mapper.select:
select * from restaurant 
join restaurantmanager on rm_rt_num = rt_num
where rm_num=#{rm_num}

mapper.update:
update rescoupon set
	rec_content = #{rec_content}, 
	rec_state = #{rec_state}, 
	rec_period = #{rec_period}
where rec_num=#{rec_num}

update resnews set
	rn_content = #{rn_content}, 
	rn_state = #{rn_state}
where rn_num=#{rn_num}

update restaurantmanager set
	rm_email = #{rm_email},
	rm_name = #{rm_name},
	rm_phone = #{rm_phone}
where rm_num = #{rm_num}

update restaurantfacility set

		rf_fa_num = #{rf_fa_num}, 

	rf_detail=#{rf_detail}
where rf_num=#{rf_num}

update rescoupon set
	rec_state = #{rec_state}
where rec_period =#{rec_period}
