mapper.select:
select * from restaurant 
join restaurantmanager on rm_rt_num = rt_num
where rm_num=#{rm_num}

select * from restaurant 
LEFT join restaurantdetail on rd_rt_num = rt_num 
where rt_num=#{rt_num}

mapper.update:
update rescoupon set
	rec_content = #{rec_content}, 
	rec_state = #{rec_state}, 
	rec_period = #{rec_period}
where rec_num=#{rec_num}

update resnews set
	rn_content = #{rn_content}, 
	rn_state = #{rn_state}
where rn_num=#{rn_num}

update restaurantfacility set

		rf_fa_num = #{rf_fa_num}, 

	rf_detail=#{rf_detail}
where rf_num=#{rf_num}
