mapper.update:
    UPDATE User
    SET us_nickname = #{us_nickname},
			us_email = #{us_email},
			us_phone = #{us_phone}
    WHERE us_id = #{us_id}

mapper.select:
select * from usfollow 
join restaurant on uf_foreign = rt_num
where uf_type = 'RESTAURANT' and uf_us_num = #{us_num}

mapper.insert:
INSERT INTO review_score (
	rs_rev_num,
	rs_st_num,
	rs_score
)
VALUES (
	#{rs_rev_num},
	#{rs_st_num},
	#{rs_score}
)

mapper.select:
select * from Restaurant 
left join RestaurantDetail on rt_num = rd_rt_num
where rt_accept = "Y" 

	and rt_dreg_num = #{cri.rt_dreg_num}


	and rt_dfc_num = #{cri.rt_dfc_num}


ORDER BY ${cri.orderBy}


ORDER BY rt_num desc

limit #{cri.pageStart}, #{cri.perPageNum}

select count(*) from Restaurant where rt_accept = "Y" 

	and rt_dreg_num = #{cri.rt_dreg_num}


	and rt_dfc_num = #{cri.rt_dfc_num}
